import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { api } from '../contexts/AuthContext';
import {
  DocumentTextIcon,
  EyeIcon,
  CameraIcon,
  CheckCircleIcon,
  XCircleIcon,
  ClockIcon,
  BuildingOfficeIcon,
  CpuChipIcon
} from '@heroicons/react/24/outline';
import LoadingSpinner from '../components/LoadingSpinner';

const InsuranceDashboard = () => {
  const [selectedReport, setSelectedReport] = useState(null);
  const [showAnalysisModal, setShowAnalysisModal] = useState(false);
  const [vlmAnalysis, setVlmAnalysis] = useState(null);
  const [filter, setFilter] = useState('all');

  const { data: reports, isLoading, error } = useQuery('insuranceReports', api.getReportsForInsurance);
  const queryClient = useQueryClient();

  const analyzePhotosMutation = useMutation(api.analyzePhotos, {
    onSuccess: (data) => {
      setVlmAnalysis(data);
    }
  });

  const createAnalysisMutation = useMutation(api.createInsuranceAnalysis, {
    onSuccess: () => {
      queryClient.invalidateQueries('insuranceReports');
      setShowAnalysisModal(false);
      setSelectedReport(null);
      setVlmAnalysis(null);
    }
  });

  if (isLoading) return <LoadingSpinner text="Loading reports..." />;
  if (error) return <div className="text-danger-600">Error loading reports</div>;

  const filteredReports = reports?.filter(report => {
    if (filter === 'all') return true;
    if (filter === 'pending') return !report.insurance_analysis;
    if (filter === 'analyzed') return report.insurance_analysis;
    return true;
  }) || [];

  const getClaimStatusBadge = (status) => {
    const badges = {
      approved: 'badge-success',
      denied: 'badge-danger',
      pending_investigation: 'badge-under-review',
    };
    return badges[status] || 'badge-under-review';
  };

  const getClaimStatusText = (status) => {
    const texts = {
      approved: 'Approved',
      denied: 'Denied',
      pending_investigation: 'Under Investigation',
    };
    return texts[status] || 'Pending';
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center space-x-3 mb-4">
          <BuildingOfficeIcon className="h-8 w-8 text-police-600" />
          <h1 className="text-3xl font-bold text-gray-900">Insurance Portal</h1>
        </div>
        <p className="text-gray-600">
          Analyze accident reports and process insurance claims using VLM technology.
        </p>
      </div>

      {/* Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="card">
          <div className="card-body text-center">
            <div className="flex justify-center mb-2">
              <ClockIcon className="h-8 w-8 text-warning-500" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900">
              {reports?.filter(r => !r.insurance_analysis).length || 0}
            </h3>
            <p className="text-sm text-gray-600">Pending Analysis</p>
          </div>
        </div>

        <div className="card">
          <div className="card-body text-center">
            <div className="flex justify-center mb-2">
              <CheckCircleIcon className="h-8 w-8 text-success-500" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900">
              {reports?.filter(r => r.insurance_analysis?.claim_status === 'approved').length || 0}
            </h3>
            <p className="text-sm text-gray-600">Claims Approved</p>
          </div>
        </div>

        <div className="card">
          <div className="card-body text-center">
            <div className="flex justify-center mb-2">
              <XCircleIcon className="h-8 w-8 text-danger-500" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900">
              {reports?.filter(r => r.insurance_analysis?.claim_status === 'denied').length || 0}
            </h3>
            <p className="text-sm text-gray-600">Claims Denied</p>
          </div>
        </div>

        <div className="card">
          <div className="card-body text-center">
            <div className="flex justify-center mb-2">
              <DocumentTextIcon className="h-8 w-8 text-gray-400" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900">
              {reports?.length || 0}
            </h3>
            <p className="text-sm text-gray-600">Total Reports</p>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="mb-6">
        <div className="flex space-x-4">
          {[
            { key: 'all', label: 'All Reports' },
            { key: 'pending', label: 'Pending Analysis' },
            { key: 'analyzed', label: 'Analyzed' }
          ].map(filterOption => (
            <button
              key={filterOption.key}
              onClick={() => setFilter(filterOption.key)}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
                filter === filterOption.key
                  ? 'bg-police-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              {filterOption.label}
            </button>
          ))}
        </div>
      </div>

      {/* Reports List */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-xl font-semibold text-gray-900">
            Reports for Analysis ({filteredReports.length})
          </h2>
        </div>
        <div className="card-body">
          {filteredReports.length > 0 ? (
            <div className="space-y-4">
              {filteredReports.map((report) => (
                <div
                  key={report.id}
                  className="border border-gray-200 rounded-lg p-6 hover:bg-gray-50 transition-colors duration-200"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-2">
                        <DocumentTextIcon className="h-5 w-5 text-police-600" />
                        <h3 className="text-lg font-medium text-gray-900">
                          Report #{report.id}
                        </h3>
                        {report.insurance_analysis && (
                          <span className={`badge ${getClaimStatusBadge(report.insurance_analysis.claim_status)}`}>
                            {getClaimStatusText(report.insurance_analysis.claim_status)}
                          </span>
                        )}
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
                        <div>
                          <p className="text-sm text-gray-500">Citizen</p>
                          <p className="font-medium">{report.user?.full_name}</p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-500">Vehicle</p>
                          <p className="font-medium">
                            {report.vehicle_make} {report.vehicle_model}
                          </p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-500">Date</p>
                          <p className="font-medium">
                            {new Date(report.accident_date).toLocaleDateString()}
                          </p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-500">Photos</p>
                          <p className="font-medium flex items-center space-x-1">
                            <CameraIcon className="h-4 w-4" />
                            <span>{report.photos?.length || 0}</span>
                          </p>
                        </div>
                      </div>

                      {/* PDRM Statement */}
                      {report.pdrm_statement && (
                        <div className="bg-police-50 border border-police-200 rounded-md p-3 mb-4">
                          <p className="text-sm text-police-800 font-medium mb-1">
                            PDRM Statement (Case: {report.pdrm_statement.case_number})
                          </p>
                          <p className="text-sm text-police-700 mb-2">
                            <strong>Fault:</strong> {report.pdrm_statement.fault_determination}
                          </p>
                          <p className="text-sm text-police-700">
                            {report.pdrm_statement.officer_findings}
                          </p>
                        </div>
                      )}

                      {/* Insurance Analysis */}
                      {report.insurance_analysis && (
                        <div className="bg-gray-50 border border-gray-200 rounded-md p-3 mb-4">
                          <div className="flex items-center justify-between mb-2">
                            <p className="text-sm text-gray-800 font-medium">
                              Insurance Analysis
                            </p>
                            <div className="flex items-center space-x-2">
                              <span className="text-sm text-gray-600">
                                Consistency Score:
                              </span>
                              <span className={`text-sm font-medium ${
                                report.insurance_analysis.consistency_score >= 0.7 
                                  ? 'text-success-600' 
                                  : report.insurance_analysis.consistency_score >= 0.4
                                  ? 'text-warning-600'
                                  : 'text-danger-600'
                              }`}>
                                {(report.insurance_analysis.consistency_score * 100).toFixed(0)}%
                              </span>
                            </div>
                          </div>
                          {report.insurance_analysis.claim_amount && (
                            <p className="text-sm text-gray-700 mb-2">
                              <strong>Claim Amount:</strong> RM {report.insurance_analysis.claim_amount.toLocaleString()}
                            </p>
                          )}
                          <p className="text-sm text-gray-700">
                            {report.insurance_analysis.damage_assessment}
                          </p>
                        </div>
                      )}
                    </div>

                    <div className="flex flex-col space-y-2 ml-4">
                      <button
                        onClick={() => setSelectedReport(report)}
                        className="btn-secondary flex items-center space-x-2"
                      >
                        <EyeIcon className="h-4 w-4" />
                        <span>View</span>
                      </button>

                      {!report.insurance_analysis && report.photos && report.photos.length > 0 && (
                        <button
                          onClick={() => {
                            setSelectedReport(report);
                            setShowAnalysisModal(true);
                          }}
                          className="btn-primary flex items-center space-x-2"
                        >
                          <CpuChipIcon className="h-4 w-4" />
                          <span>Analyze</span>
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <DocumentTextIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No reports found</h3>
              <p className="text-gray-600">
                {filter === 'all' 
                  ? 'No reports are available for insurance review.'
                  : `No reports with status "${filter}" found.`
                }
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Report Details Modal */}
      {selectedReport && !showAnalysisModal && (
        <ReportDetailsModal
          report={selectedReport}
          onClose={() => setSelectedReport(null)}
        />
      )}

      {/* VLM Analysis Modal */}
      {showAnalysisModal && selectedReport && (
        <VLMAnalysisModal
          report={selectedReport}
          vlmAnalysis={vlmAnalysis}
          onClose={() => {
            setShowAnalysisModal(false);
            setSelectedReport(null);
            setVlmAnalysis(null);
          }}
          onAnalyze={analyzePhotosMutation.mutate}
          onSubmit={createAnalysisMutation.mutate}
          analyzing={analyzePhotosMutation.isLoading}
          submitting={createAnalysisMutation.isLoading}
        />
      )}
    </div>
  );
};

// Report Details Modal Component
const ReportDetailsModal = ({ report, onClose }) => (
  <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
    <div className="relative top-20 mx-auto p-5 border w-11/12 max-w-4xl shadow-lg rounded-md bg-white">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-medium text-gray-900">
          Report #{report.id} - Insurance Review
        </h3>
        <button
          onClick={onClose}
          className="text-gray-400 hover:text-gray-600"
        >
          ×
        </button>
      </div>

      <div className="space-y-6 max-h-96 overflow-y-auto">
        {/* All the same content as PDRM modal but with insurance focus */}
        <div>
          <h4 className="font-medium text-gray-900 mb-2">Claim Information</h4>
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <span className="text-gray-500">Policy Holder:</span>
              <span className="ml-2">{report.user?.full_name}</span>
            </div>
            <div>
              <span className="text-gray-500">Vehicle:</span>
              <span className="ml-2">{report.vehicle_year} {report.vehicle_make} {report.vehicle_model}</span>
            </div>
          </div>
        </div>

        {/* Photos for analysis */}
        {report.photos && report.photos.length > 0 && (
          <div>
            <h4 className="font-medium text-gray-900 mb-2">Evidence Photos ({report.photos.length})</h4>
            <div className="grid grid-cols-3 gap-2">
              {report.photos.map((photo, index) => (
                <img
                  key={index}
                  src={`/uploads/${photo.filename}`}
                  alt={`Evidence ${index + 1}`}
                  className="w-full h-24 object-cover rounded border"
                />
              ))}
            </div>
          </div>
        )}

        {/* PDRM Statement */}
        {report.pdrm_statement && (
          <div>
            <h4 className="font-medium text-gray-900 mb-2">PDRM Assessment</h4>
            <div className="bg-police-50 p-3 rounded">
              <p className="text-sm"><strong>Case Number:</strong> {report.pdrm_statement.case_number}</p>
              <p className="text-sm"><strong>Fault Determination:</strong> {report.pdrm_statement.fault_determination}</p>
              <p className="text-sm mt-2">{report.pdrm_statement.officer_findings}</p>
            </div>
          </div>
        )}

        {/* Insurance Analysis if exists */}
        {report.insurance_analysis && (
          <div>
            <h4 className="font-medium text-gray-900 mb-2">Insurance Analysis</h4>
            <div className="bg-gray-50 p-3 rounded">
              <p className="text-sm"><strong>Claim Status:</strong> {getClaimStatusText(report.insurance_analysis.claim_status)}</p>
              <p className="text-sm"><strong>Consistency Score:</strong> {(report.insurance_analysis.consistency_score * 100).toFixed(0)}%</p>
              {report.insurance_analysis.claim_amount && (
                <p className="text-sm"><strong>Claim Amount:</strong> RM {report.insurance_analysis.claim_amount.toLocaleString()}</p>
              )}
              <p className="text-sm mt-2">{report.insurance_analysis.vlm_photo_analysis}</p>
            </div>
          </div>
        )}
      </div>

      <div className="mt-6 flex justify-end">
        <button onClick={onClose} className="btn-secondary">
          Close
        </button>
      </div>
    </div>
  </div>
);

// VLM Analysis Modal Component
const VLMAnalysisModal = ({ report, vlmAnalysis, onClose, onAnalyze, onSubmit, analyzing, submitting }) => {
  const [formData, setFormData] = useState({
    accident_report_id: report.id,
    vlm_photo_analysis: '',
    damage_assessment: '',
    consistency_score: 0,
    claim_status: 'pending_investigation',
    claim_amount: '',
    notes: ''
  });

  const handleAnalyze = () => {
    const photoUrls = report.photos.map(photo => `/uploads/${photo.filename}`);
    onAnalyze({
      photo_urls: photoUrls,
      damage_description: report.damage_description,
      incident_description: report.incident_description
    });
  };

  React.useEffect(() => {
    if (vlmAnalysis) {
      setFormData(prev => ({
        ...prev,
        vlm_photo_analysis: vlmAnalysis.analysis,
        damage_assessment: vlmAnalysis.damage_assessment,
        consistency_score: vlmAnalysis.consistency_score
      }));
    }
  }, [vlmAnalysis]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const submitData = {
      ...formData,
      claim_amount: formData.claim_amount ? parseFloat(formData.claim_amount) : null
    };
    onSubmit(submitData);
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-10 mx-auto p-5 border w-11/12 max-w-4xl shadow-lg rounded-md bg-white">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium text-gray-900">
            VLM Analysis - Report #{report.id}
          </h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            ×
          </button>
        </div>

        <div className="space-y-6">
          {/* Photos */}
          <div>
            <h4 className="font-medium text-gray-900 mb-2">Photos for Analysis</h4>
            <div className="grid grid-cols-4 gap-2 mb-4">
              {report.photos.map((photo, index) => (
                <img
                  key={index}
                  src={`/uploads/${photo.filename}`}
                  alt={`Analysis ${index + 1}`}
                  className="w-full h-20 object-cover rounded border"
                />
              ))}
            </div>

            {!vlmAnalysis && (
              <button
                onClick={handleAnalyze}
                disabled={analyzing}
                className="btn-primary flex items-center space-x-2"
              >
                <CpuChipIcon className="h-4 w-4" />
                <span>{analyzing ? 'Analyzing...' : 'Run VLM Analysis'}</span>
              </button>
            )}
          </div>

          {/* Analysis Form */}
          {vlmAnalysis && (
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="form-label">VLM Analysis Results</label>
                <textarea
                  value={formData.vlm_photo_analysis}
                  onChange={(e) => setFormData({...formData, vlm_photo_analysis: e.target.value})}
                  rows="6"
                  className="form-textarea"
                  readOnly
                />
              </div>

              <div>
                <label className="form-label">Damage Assessment</label>
                <textarea
                  value={formData.damage_assessment}
                  onChange={(e) => setFormData({...formData, damage_assessment: e.target.value})}
                  rows="3"
                  className="form-textarea"
                  required
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="form-label">Consistency Score</label>
                  <input
                    type="number"
                    step="0.01"
                    min="0"
                    max="1"
                    value={formData.consistency_score}
                    onChange={(e) => setFormData({...formData, consistency_score: parseFloat(e.target.value)})}
                    className="form-input"
                    required
                  />
                </div>

                <div>
                  <label className="form-label">Claim Status</label>
                  <select
                    value={formData.claim_status}
                    onChange={(e) => setFormData({...formData, claim_status: e.target.value})}
                    className="form-select"
                    required
                  >
                    <option value="pending_investigation">Pending Investigation</option>
                    <option value="approved">Approved</option>
                    <option value="denied">Denied</option>
                  </select>
                </div>
              </div>

              <div>
                <label className="form-label">Claim Amount (RM)</label>
                <input
                  type="number"
                  step="0.01"
                  min="0"
                  value={formData.claim_amount}
                  onChange={(e) => setFormData({...formData, claim_amount: e.target.value})}
                  className="form-input"
                  placeholder="Enter claim amount if approved"
                />
              </div>

              <div>
                <label className="form-label">Additional Notes</label>
                <textarea
                  value={formData.notes}
                  onChange={(e) => setFormData({...formData, notes: e.target.value})}
                  rows="3"
                  className="form-textarea"
                  placeholder="Any additional notes or comments..."
                />
              </div>

              <div className="flex justify-end space-x-3 pt-4">
                <button type="button" onClick={onClose} className="btn-secondary">
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={submitting}
                  className="btn-primary"
                >
                  {submitting ? 'Submitting...' : 'Submit Analysis'}
                </button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default InsuranceDashboard;
